
from django.test import TestCase
from ..models import Gene, Protein


class GeneTest(TestCase):
    """ Test module for Puppy model """

    def setUp(self):
        Gene.objects.create(
            name='TP53', sequence='')
    
    def test_gene_sequence(self):
        gene_TP53 = Gene.objects.get(name='TP53')
        self.assertEqual(
            gene_TP53.get_sequence(), "TP53 has sequence .")

class ProteinTest(TestCase):
    """ Test module for Puppy model """

    def setUp(self):
        Gene.objects.create(
            name='TP53', sequence='')
        gene_TP53 = Gene.objects.get(name='TP53')
        Protein.objects.create(
            gene=gene_TP53, name='isoform G', sequence='')
    
    def test_gene_sequence(self):
        protein_isoform_G = Protein.objects.get(name='isoform G')
        self.assertEqual(
            protein_isoform_G.get_sequence(), "isoform G has sequence .")